import axios from "axios";
import React, { createContext, useState } from "react";

export const AppContext = createContext();

const AppContextProvider = (props) => {
  // State
  const [accountNumber, setAccountNumber] = useState("");
  const [availablebanks, setAvailableBanks] = useState([]);

  //   Fetch available banks with paystacks bank get API
  const fetchBanks = () => {
    axios
      .get("https://api.paystack.co/bank?currency=NGN", {
        headers: {
          Authorization: `Bearer ${process.env.PAYSTACK_TEST_SECRET_KEY}`,
        },
      })
      .then((res) => {
        console.log(res, "Banks");
        setAvailableBanks(res.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <AppContext.Provider
      value={{ accountNumber, setAccountNumber, bank, setBank, fetchBanks }}
    >
      {props.children}
    </AppContext.Provider>
  );
};

export default AppContextProvider;
