import { faBars } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from "react";
import { Link } from "react-router-dom";
import classes from "./Header.module.css";

const Header = () => {
  return (
    <section className={classes.outerContainer}>
      <div className={classes.container}>
        <div className={classes.logoSection}>Integrations Assessment</div>
        <div className={classes.navLinkSection}>
          <Link to="/">Transfer</Link>
          <Link to="/transaction-history">Project Documentation</Link>
        </div>
        <div className={classes.hamburgerMenu}>
          <FontAwesomeIcon icon={faBars} />
        </div>
      </div>

      {/* Sidenav */}
      <div className={classes.sideNav} id="sideMenu">
        <div className={classes.sideNavInner}>
          <div>
            <FontAwesomeIcon icon={faXmark} onClick={closeSideMenu} />
          </div>
          <div>
            <img src={logo} alt="Cloudpact Logo" />
          </div>
          <Link to="/blogSection">Home</Link>
          <Link to="/blogSection/contact">Contact</Link>
          <div
            onClick={() => {
              // A function to set show services dropdown to true so the services box appears
              setShowServicesDropdown(!showServivesDropdown);
            }}
          >
            <span>Services</span>
            <span
              style={
                showServivesDropdown
                  ? {
                      transform: "rotate(-90deg)",
                      transition: "all 0.2s ease-in-out",
                    }
                  : {
                      transform: "rotate(0deg)",
                      transition: "all 0.2s ease-in-out",
                    }
              }
            >
              <FontAwesomeIcon icon={faAngleDown} />
            </span>

            {/* This box shows conditionally when showServicesDropdown is set to true */}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Header;
