import React, { useContext } from "react";
import Card from "../../Components/Card/Card";
import Layout from "../../Components/Layout/Layout";
import classes from "./InitiateTransfer.module.css";
import Input from "../../Components/Input/Input";
import { AppContext } from "../../Context/AppContext";

const InitiateTransfer = () => {
  // context
  const { accountNumber, setAccountNumber } = useContext(AppContext);
  return (
    <Layout>
      <section className={classes.container}>
        <div className={classes.innerContainer}>
          <Card>
            <h3>Make a transfer</h3>
            <div className={classes.transferForm}>
              {/* Account number input */}
              <Input id="accountNumber" value={accountNumber}>
                <label htmlFor="accountNumber" style={{ cursor: "text" }}>
                  Bank Account Number
                </label>
              </Input>
            </div>
          </Card>
        </div>
      </section>
    </Layout>
  );
};

export default InitiateTransfer;
